mapper.select:
SELECT count(id)
FROM (
SELECT SAN_REVIEW_ID AS id
FROM SAN_REVIEW_V
WHERE USER_ID = #{userId}
UNION
SELECT SAN_PARTY_ID AS id
FROM SAN_PARTY_V
WHERE USER_ID = #{userId}
) post

mapper.update:
UPDATE user_tb
SET nickname= #{nickname}
WHERE user_id = #{userId}

mapper.select:

SELECT count(san_party_id)
FROM san_party_tb
WHERE TO_CHAR(SYSDATE, 'yyyy/MM/dd HH:MM') <= TO_CHAR(PARTY_DT, 'yyyy/MM/dd HH:MM') AND user_id=#{userId}

SELECT count(san_party_id)
FROM san_party_tb
WHERE TO_CHAR(SYSDATE, 'yyyy/MM/dd HH:MM') > TO_CHAR(PARTY_DT, 'yyyy/MM/dd HH:MM') AND user_id=#{userId}

mapper.update:
  		UPDATE user_tb
		SET address=#{address}, san_range=#{sanRange}, san_taste=#{sanTaste}
		WHERE user_id=#{userId}
